    @Test
    public void parseZerocoinSpendScript(){
        String hex = "c2" + // OP_ZEROCOINSPENT
                "02ef4a" + // OP_NUM + REVERSED DATA SIZE.
                "";
        byte[] bytes = Hex.decode(hex);
        byte[] sizeBytes = Utils.reverseBytes(Arrays.copyOfRange(bytes,2,4));
        BigInteger size = new BigInteger(Hex.toHexString(sizeBytes),16);
        System.out.println("data size: " + Hex.toHexString(sizeBytes) + " --> " + size);

        byte[] coinSpendBytes = Arrays.copyOfRange(bytes,4,bytes.length);
        System.out.println("Spend hex: " + Hex.toHexString(coinSpendBytes));
        CoinSpend coinSpend = CoinSpend.parse(
                params,
                zerocoinContext,
                coinSpendBytes
        );

        System.out.println(coinSpend);
    }


